import { useEffect, useState } from 'react'
import './Home.css'
import axios from 'axios';


const Home = () => {
    const data = './tshirt.jpg'
    const [image2, setImage2] = useState(data);
    const [selectedImage, setSelectedImage] = useState(null);
    const [result, setResult] = useState([]);
    const onUploadChange = async(e) => {
        setSelectedImage( e.target.files[0]);
    }

    const hitApi = async() => {
        const formData = new FormData();
        formData.append("file", selectedImage);
        try {
          const response = await axios({
            method: "post",
            url: "http://3.7.131.209:8000/api/pantones",
            body: formData,
            headers: { "Content-Type": "multipart/form-data" },
          });
          console.log(response);
        } catch(error) {
          console.log(error)
        }
    }

    return (
        <div>
            <div>
                <div>
                    <div className='mt-5 text-center'>
                        <div className='mb-1'>Upload Image here to get the pantone colour codes</div>
                        <input type='file' name='file' onChange={(e) => onUploadChange(e)} className='p-inputtext p-inputtext-sm w-full' accept='.png,.jpeg,.jpg,.svg' style={{ backgroundColor: 'beige' }} />
                        <button onClick={hitApi}>Upload</button>
                    </div>
                    <div className='mt-2'>
                        {image2 && (
                            <div className='mt-3 text-center'>
                                <img src={image2} alt='No File Uploaded' width='250' height='300' style={{ border: '1px solid gray' }} />
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home
