import { useEffect, useState } from 'react'
import './Home.css'
import axios from 'axios';


const Home = () => {
    const data = './tshirt.jpg'
    const [image2, setImage2] = useState(data);

    const onUploadChange = (e) => {
        const selectedImage = e.target.files[0];
        const reader = new FileReader();
        reader.onload = () => {
            setImage2(reader.result);
        }
        reader.readAsDataURL(selectedImage);

    }

    return (
        <div>
            <div>
                <div>
                    <div className='mt-5 text-center'>
                        <div className='mb-1'>Upload Image here to get the pantone colour codes</div>
                        <input type='file' name='file' onChange={(e) => onUploadChange(e)} className='p-inputtext p-inputtext-sm w-full' accept='.png,.jpeg,.jpg,.svg' style={{ backgroundColor: 'beige' }} />
                    </div>
                    <div className='mt-2'>
                        {image2 && (
                            <div className='mt-3 text-center'>
                                <img src={image2} alt='No File Uploaded' width='250' height='300' style={{ border: '1px solid gray' }} />
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home
