import React, { useRef, useState } from 'react';
import { Toast } from 'primereact/toast';
import { FileUpload } from 'primereact/fileupload';
import { Tooltip } from 'primereact/tooltip';

function Home  () {
  const removeUpload = () => {
    fileInput.current.clear();
    formik.setFieldValue('file', '');
};

const selectHandler = (event) => {
    formik.setFieldValue('file', event.files[0]);
};
return (
    <div>
        <div className='flex align-items-center justify-content-left'>
            <FileUpload
                name='fileName'
                mode='basic'
                multiple={false}
                maxFileSize='100000'
                accept='.csv'
                onSelect={selectHandler}
                customUpload
                label='Choose file'
                className='mr-2 mb-3'
                // style={{ width: '50%' }}
                chooseLabel={formik.values.file ? formik.values.file.name : 'Choose file'}
                ref={fileInput}
            />
            <Button
                icon='pi pi-times'
                className='p-button-rounded mb-3'
                onClick={removeUpload}
                type='button'
                disabled={!(formik.values.file)}
            />
        </div>
        { formik.errors.file
        && (
            <div className='text-pink-500'>
                *
                {' '}
                {formik.errors.file}
            </div>
        )}
    </div>
    )
}

export default Home;











