import { useEffect, useState } from "react";
import "./Home.css";
import axios from "axios";

const Home = () => {
  const [image, setImage] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [data, setData] = useState([]);
  const onUploadChange = async (e) => {
    setImage(e.target.files[0]);
    setSelectedImage(URL.createObjectURL(e.target.files[0]));
  };

  const hitApi = async () => {
    const formData = new FormData();
    formData.append("image", image);
    try {
      const response = await axios.post(
        "http://localhost:8000/api/pantones",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setData(response.data);
      //     response.data.map((e) => {
      //       console.log(e["HEXACODE"]);
      //   });
      // console.log(typeof(response));
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <div className="mt-5 text-center">
        <div className="mb-1">
          Upload Image here to get the pantone colour codes
        </div>
        <input
          type="file"
          name="file"
          onChange={(e) => onUploadChange(e)}
          className="p-inputtext p-inputtext-sm w-full"
          accept=".png,.jpeg,.jpg,.svg"
          style={{ backgroundColor: "beige" }}
        />
        <button onClick={hitApi}>Upload</button>
      </div>
      <div className="mt-2">
        {selectedImage && (
          <div className="mt-3 text-center">
            <img
              src={selectedImage}
              alt="No File Uploaded"
              width="250"
              height="300"
              style={{ border: "1px solid gray" }}
            />
          </div>
        )}
        <div>
          <table>
            <tr>
              <th>Hexcode</th>
              <th>colour</th>
              <th>C-value</th>
              <th>Y-value</th>
              <th>M-value</th>
              {/* <th>K-value</th>
            <th>W-value</th> */}
            </tr>

            {data.map((element, index) => {
              return (
                <tr key={index}>
                  <td>{element["HEXACODE"]}</td>
                  <td>
                    <div
                      style={{
                        width: "10px",
                        height: "10px",
                        backgroundColor: `#${element["HEXACODE"]}`,
                        margin: "auto",
                      }}
                    ></div>
                  </td>
                  <td>{element["RED"]}</td>
                  <td>{element["BLUE"]}</td>
                  <td>{element["GREEN"]}</td>
                </tr>
              );
            })}
          </table>
        </div>
      </div>
    </div>
  );
};

export default Home;
