import { useState } from "react";
import axios from "axios";

const Compare = () => {
  const [image, setImage] = useState(null);
  const [afterImage, setAfterImage] = useState(null);
  const [beforeImage, setBeforeImage] = useState(null);
  const [data, setData] = useState([]);

  const onImageChange = async (e) => {
    setImage(e.target.files[0]);
    setBeforeImage(URL.createObjectURL(e.target.files[0]));
  };

  const onAfterImageChange = async (e) => {
    setAfterImage(e.target.files[0]);
    setSelectedImage(URL.createObjectURL(e.target.files[0]));
  };

  const hitApi = async () => {
    const formData = new FormData();
    formData.append("image", image);
    console.log(image);
    try {
      const response = await axios.post(
        "http://localhost:8000/api/pantones",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <div className="mt-5 text-center">
        <div
          style={{
            display: "flex",
            marginRight: "25px",
          }}
        >
          <input
            type="file"
            name="file"
            onChange={(e) => onImageChange(e)}
            className="p-inputtext p-inputtext-sm w-full"
            accept=".png,.jpeg,.jpg,.svg"
            style={{ backgroundColor: "beige" }}
          />
          <input
            type="file"
            name="file"
            onChange={(e) => onAfterImageChange(e)}
            className="p-inputtext p-inputtext-sm w-full"
            accept=".png,.jpeg,.jpg,.svg"
            style={{ backgroundColor: "beige" }}
          />
        </div>
      </div>
      <div className="button-div">
        <button className="button-css" onClick={hitApi}>
          Upload
        </button>
      </div>
    </div>
  );
};

export default Compare;
